    this.setState({score});
    this.setState({clickMessage});

  }else {
    clickedMatch[0].clicked = true;
    score = 0;




  }
};

  render() {
    return (
      <Wrapper>
        <Title>Clicky Game</Title>



        {friends.map(person => <FriendCard key={person.id} {...person} />)}

      </Wrapper>
    )
  }
}


export default App;
